mapper.select:

UPDATE user 
SET user_pw = #{userPw} 
WHERE user_id = #{userId}

mapper.update:

UPDATE review
SET deleted_yn = 'Y'
WHERE idx = ${idx}


UPDATE review
SET deleted_yn = 'N'
WHERE idx = ${idx}

mapper.select:

SELECT title 
FROM review
WHERE title like ${title}% || %${title}% || %${title}
